import { blogData } from '../mockData/blogData';

export const BlogCard = ({ date, title, imgSrc, link }) => (
  <div className="blog-card">
    <img src={imgSrc} alt="Blog Post" className="blog-card__img" />
    <div className="blog-card__content">
      <p className="blog-card__date">{date}</p>
      <h3 className="blog-card__title">{title}</h3>
      <a href={link} className="blog-card__link">Читать полную статью</a>
    </div>
  </div>
);

const Blog = () => {
  return (
    <section className="blog-section">
      <div className="blog-cards__wrapper">
        {blogData.map((post, index) => (
          <BlogCard
            key={index}
            date={post.date}
            title={post.title}
            imgSrc={post.imgSrc}
            link={post.link}
          />
        ))}
      </div>
    </section>
  );
};

export default Blog;
